asked = false;
answers = new Array("i'd bet my grandchildren on it", "oh, certainly, bubbeleh", "as if hashem himself<br>said, it<br>was so", "yes &mdash; now eat something before you waste<br>away", "you're such a mensch &mdash; of course!", "your grandma ida, g-d rest her soul, would<br>say<br>yes", "as certain as anything in this terrible world", "does the rabbi tell bad<br>jokes?", "the outlook is as good as tanta rivka's rugelach", "no... oh wait, yes. i got confused<br>for a<br>moment", "eh, too tired, ask after a shluf", "don't give it a kinahora", "what am i? a future-predicting billiard<br>ball?", "maybe yes, maybe no", "what? my hearing's not so good anymore", "i wouldn't count on it, boytchik", "<br>nah", "<br>feh", "oy, not so good", "are you meshuggenah<br>?!");
num = answers.length;
q.disabled = false;
orig = true;
function ask() {
 v = q.value;
 if (v == '' || v.charAt(v.length-1) != '?') {
   alert('Please enter your question');
   return false;
 }
 asked = !asked;
 if (asked) {
  a.value = 'Reset';
  ball0.style.display = 'none';
  ball1.style.display = 'inline';
  q.disabled = true;
  r = parseInt(Math.random()*num);
  if (orig) r = parseInt(Math.random()*10);
  f = parseInt(v);
  if (f >= 1 && f <= num) r = f-1;
  if (f == -1) r = num-1;
  w = answers[r];
  if (w.length < 10) t.style.fontSize = '18px';
  else if (w.length > 25) t.style.fontSize = '12px';
  else t.style.fontSize = '14px';
  t.innerHTML = w;
 } else {
  a.value = 'Ask!';
  ball1.style.display = 'none';
  ball0.style.display = 'inline';
  q.disabled = false;
 }
}
function noOrig() {
  orig = false;
}
