var f = new Array('Dried Apricots', 'Buttered Matzah', 'Creamy Schmaltz', 'Noodle Kugel', 'Atlantic Lox', 'Hot Borscht', 'Honey Challah', 'Celery Root', 'Hanukkah Gelt', 'Hard-Boiled Egg', 'Crispy Latkes', 'Buckwheat Kasha', 'Coconut Macaroon', 'Hearty Chickpeas', 'Marble Rye', 'Salted Parsley', 'Chopped Liver', 'Pickled Herring', 'Pistachio Halvah', 'Sorrel Soup', 'Hebrew National Hot Dog', 'Smoked Whitefish', 'Deep-Fried Falafel', 'Everything Bagel', 'Cherry Blintz', 'Shredded Horseradish', 'Matzo Ball', 'Tahini Hummus', 'Gefilte Fish', 'Fig Rugelach', 'Cured Pastrami');
lastX = 0;
var t = document.getElementById("txt");
sel = new Array(3);
use = new Array(3);
counter = 0;
function restart() {
  counter = 0;
  t.innerHTML = "";
  for (i=0;i<3;i++) clear(use[i], sel[i]);
}
function clear(u, v) {
  u.style.backgroundImage = "none";
  u.style.backgroundColor = "#c0c0c0";
  u.innerHTML = v;
  lastX = 0;
}
function show(x, d) {
  if (counter == 3) {
    restart();
  }
  if (x == lastX) return;
  if (x > f.length) return;
  d.style.backgroundImage = "url('img/other/br/pic"+x+".jpg')";
  d.innerHTML = " ";
  sel[counter] = x;
  use[counter] = d;
  counter++;
  if (counter == 3) {
    t.innerHTML = f[sel[0]-1] + ", " + f[sel[1]-1] + ", " + f[sel[2]-1] + ". Enjoy!";
  }
  lastX = x;
}
