description:
Match the Jewish senator to his or her state:


Guesses Remaining
15


Score
0





<<

>>




























n = new Array("Barbara Boxer", "Benjamin Cardin", "Al Franken", "Ernest Gruening", "Chic Hecht", "Herb Kohl", "Frank Lautenberg", "Carl Levin", "Joe Lieberman", "Howard Metzenbaum", "Bernie Sanders", "Brian Schatz", "Chuck Schumer", "Arlen Specter", "Ron Wyden");
a = new Array(3, 0, 8, 11, 1, 7, 2, 4, 6, 12, 9, 14, 13, 10, 5);
l = n.length;
c = new Array(l);
d = document.getElementById("who");
cur = 7;
gr = document.getElementById("gd");
sc = document.getElementById("sd");
bs = document.getElementsByName("but");
br = String.fromCharCode(10);
guesses = 0;
score = 0;
reset();

function reset() {
  cur = 7;
  guesses = 15;
  score = 0;
  for (i=0;i<l;i++) c[i] = false;
  disable(false);
  show();
  draw();
}

function disable(d, c) {
  for (i=0;i<bs.length;i++) {
   bs[i].disabled = d;
  }
}

function show() {
  d.innerHTML = n[cur];
}

function draw() {
  gr.innerHTML = ''+guesses;
  sc.innerHTML = ''+score;
}

function next(x) {
  stop = false;
  orig = cur;
  first = true;
  while ((first || c[cur]) && !stop) {
    if (x > 0) cur++;
    else cur = cur-1;
    if (cur < 0) cur = l-1;
    else if (cur >= l) cur = 0;
    if (cur == orig) stop = true;
    first = false;
  }
  if (stop) {
    over();
  }
  show();
}

function pick(b, x) {
  guesses = guesses-1;
  if (a[x] == cur) {
    score++;
    c[cur] = true;
    b.disabled = true;
  }
  draw();
  if (guesses <= 0) {
    over();
  } else {
    next(1);
  }
}

function over() {
  disable(true);
  msg = 'Game Over. Your Score: ' + score + '.';
  if (score == 15) msg += br + 'Great job!';
  else if (score >= 13) msg += br + 'Getting close...';
  else if (score >= 11) msg += br + 'Not bad...';
  else if (score >= 9) msg += br + 'Average...';
  else if (score >= 7) msg += br + 'Could be worse...';
  else msg += br + 'Terrible!';
  if (score == 15) {
   alert(msg);
  } else {
   msg += ' Try Again?';
   ok = confirm(msg);
   if (ok) reset();
   else wrong();
  }
}

function wrong() {
  msg = '';
  for (i=0;i<l;i++) {
    v = bs[i].value;
    w = a[i];
    if (!c[w]) {
      msg += v + ": " + n[w] + br;
    }
  }
  alert(msg);
}
