description:
Is Charles Bronson Jewish? No. Both his parents were goyishe Lithuanian. Now that that's settled, it's time to play... 
DEATH WISH VI:This Time It's Rabbinical

Instructions: shoot the bad guys with your gun mouse.Don't shoot the rabbis!




 Score:  



















var score, target, timer, curr;
var ie = navigator.appName.indexOf("Microsoft") != -1;
ho = document.getElementById("holder");
sc = document.getElementById("sdiv");
gv = document.getElementById("gover");
ba = document.getElementById("bang");
en = document.getElementById("enemy");
function begin() {
ho.style.backgroundColor = "#FFFFFF";
score = 0;
curr = 0;
gover.innerHTML = '';
hideDiv(intro);
hideDiv(restart);
showDiv(scene);
disp();
showDiv(enemy);
loadPic();
}
function loadPic(x) {
curr++;
y = x;
if (x == null) y = rand(2);
target = y;
targ.src = 'img/other/cb/bronson'+y+'.gif';
en.style.left = rand(250);
en.style.top = rand(170);
timer = 0;
if (y == 2) timer = getTime();
if (y != 2) setTimeout('hold(curr)', rand(500)+500);
}
function kill() {
if (target == 1) { die(1); return; }
if (target == 2) add(10);
setTimeout('reset()', 300);
}
function hold(holding) {
if (holding == curr) loadPic();
}
function shoot(ev) {
x = ev.clientX-55;       
y = ev.clientY-55;
ba.style.left = x;
ba.style.top = y;
newTime = getTime();
dt = 1200 - curr * 5;
if (dt < 200) dt = 200;
if (timer > 0 && newTime - timer > dt) { die(0); return; }
showDiv(bang);
kill();
}
function reset() {
hideDiv(bang);
loadPic();
}
function die(how) {
hideDiv(enemy);
hideDiv(bang);
ho.style.backgroundColor = "#FF0000";
t = "<p align=center><span class=largeText>"
if (how == 0) t += "You are not fast enough!"
if (how == 1) t += "You shot a rabbi!"
t += "<br><br></span>GAME OVER</p>";
gv.innerHTML = t;
showDiv(restart);
}
function getTime() { return (new Date()).getTime(); }
function add(x) { score += x; disp(); }
function disp() { sc.innerHTML = score; }
function rand(x) { return parseInt(Math.random()*x)+1; }
