description:
Can you name all Jewish laureates of the Nobel Prize in Literature?





...1910Germany
...1927France
...1958USSR
...1966Israel
...1966Sweden
...1976USA
...1978USA
...1981UK
...1987USA
...1991S. Africa
...2002Hungary
...2004Austria
...2005UK
...2014France
...2016USA
...2020USA


answer = new Array("Paul von Heyse", "Henri Bergson", "Boris Pasternak", "Shmuel Yosef Agnon", "Nelly Sachs", "Saul Bellow", "Isaac Bashevis Singer", "Elias Canetti", "Joseph Brodsky", "Nadine Gordimer", "Imre Kertesz", "Elfriede Jelinek", "Harold Pinter", "Patrick Modiano", "Bob Dylan", "Louis Gluck");
answer2 = new Array("von Heyse", "Henri-Louis Bergson", "", "Shai Agnon", "", "Solomon Bello", "", "", "Iosif Brodsky", "", "Kertesz Imre", "", "", "", "", "");
done = new Array(answer.length);
correct = 0;
d1 = document.tf.a1;
d1.value = '';
d1.disabled = false;
tot = answer.length;

function check(s) {
v = s.toLowerCase().trim();
if (v == "") return;
for (i=0;i<tot;i++) {
  if (answer[i].toLowerCase() == v) return i;
  if (answer2[i].toLowerCase() == v) return i;
  aa = answer[i].split(" ");
  if (aa[aa.length-1].toLowerCase() == v) return i;
}
return -1;
}

function go() {
if (d1.disabled) return;
a = d1.value;
if (a == "") { alert("Take a guess!"); return; }
if (a.toLowerCase() == "churchill") { alert("Impeach Churchill!"); return; }
q = check(a);
if (q == -1) { alert("Try again!"); return; }
d = eval('c'+q);
if (d != null) d.innerHTML = answer[q];
if (!done[q]) correct++;
done[q] = true;
d1.value = '';
finish(false);
}

function show() {
for (q=0;q<tot;q++) {
  d = eval('c'+q);
  if (d != null && !done[q]) d.innerHTML = '<i>'+answer[q]+'</i>';
}
}

function giveup() {
if (d1.disabled) return;
msg = 'Are you sure you want to give up?';
if (correct == 0) msg += ' One answer should be pretty obvious!';
else if (correct < 4) msg += ' There are answers on this page!';
ok = confirm(msg);
if (ok) { show(); finish(true); }
}

function finish(force) {
if (correct == tot || force) d1.disabled = true;
if (correct == tot) d1.value = 'Congratulations!';
if (force) d1.value = 'Score: '+correct+'/'+tot;
}
