description:
Tell us, what do you see?


can = document.getElementById("canvas");
ctx = can.getContext("2d");
bar = document.getElementById("babs");
phr = document.getElementById("phrase");
but = document.getElementById("b1");
ans = new Array("Tell us, what do you see?", "Interesting...", "Intriguing...", "A few more...", "Strange...", "Never heard that one before...", "We're getting a clearer picture...", "Doesn't Hermann Rorschach look like Brad Pitt?...", "That's the same answer Ted Bundy gave...", "OK, one more...", "Diagnosis: you're obsessed with Barbra Streisand.");
num = 0;
draw();

function draw() {
  num++;
  phr.innerHTML = ans[num-1];
  if (num == 11) {
    b1.value = 'Restart';
    num = 0;
    return;
  } else if (num == 10) {
    barbra(true);
    return;
  } else if (num == 1) {
    b1.value = 'Answer';
    barbra(false);
  }
  ctx.clearRect(0, 0, 350, 350);
  q = parseInt(Math.random()*20)+55;
  for (i=0;i<q;i++) circle();
}

function barbra(b) {
  can.style.display = !b ? 'inline' : 'none';
  bar.style.display = b ? 'inline' : 'none';
}


function circle() {
  x = parseInt(Math.random()*150)+25;
  y = parseInt(Math.random()*300)+25;
  r = parseInt(Math.random()*20)+5;
  ctx.beginPath();
  ctx.arc(x, y, r, 0, Math.PI*2, true);
  ctx.closePath();
  ctx.fill();
  ctx.beginPath();
  ctx.arc(350-x, y, r, 0, Math.PI*2, true);
  ctx.closePath();
  ctx.fill();
}
