.dial {color:#ffff00; font-weight:bold; text-align:center; 
font-size:20px}
.buttons {text-align:center;}

Since we're in a gambling mood, here's $10 virtual to play our slot machine. Press SPIN and watch your money disappear multiply! Get triples to get cash, bagels are wild... Beware the Gibson!

(This used to be the Donald Trump profile, but we moved it here. We really should have said, Beware the Trump...)








































var credits, pet, paid;
interval = 100;
over = false;
spinning = false;
counter = 0;
stopper = 0;
var c1, c2, c3;

restart();
show();

function rand(x) {
return parseInt(Math.random()*x);
}

function maxbet() {
b = 5;
if (credits > 10) b = 10;
if (credits > 50) b = 50;
if (credits > 99) b = 99;
return b;
}

function show() {
if (credits < 0) credits = 0;
tc.innerHTML = credits;
tb.innerHTML = bet;
tp.innerHTML = paid;
}

function restart() {
credits = 10;
bet = 1;
paid = 0;
}

function press(b) {
if (spinning) return;
if (credits <= 0) over = true;
if (over && b != 0) {
  alert("Game Over!");
  return;
}
if (b == 0) {
  if (confirm("This will restart the game. Are you sure?")) {
  over = false;
  restart();
  }
} else if (b == 1) {
  if (confirm("This will end the game. Are you sure?")) {
  over = true;
  alert("You won $"+credits+"!");
  }
} else if (b == 2) {
  bet += 1;
  if (bet > maxbet()) bet = 1;
} else if (b == 3) {
  bet = maxbet();
  if (bet > credits) bet = credits;
} else if (b == 4) {
  spinning = true;
  if (bet > credits) bet = credits;
  credits -= bet;
  counter = 0;
  setTimeout('spin()', interval);
}
show();
}

function draw(p, x) {
x1 = x != 6 ? x : "bagel"; 
u = 'img/ratings/rating-';
p.src = u + x1 + '.jpg';
}

function spin() {
f = 'spin()';
if (counter == 0) stopper = rand(10)+15;
if (counter == stopper) f = 'stop()';
if (counter < stopper - 10) {
  c1 = rand(7);
  draw(r1, c1);
}
if (counter < stopper - 5) {
  c2 = rand(7);
  draw(r2, c2);
}
c3 = rand(7);
draw(r3, c3);
counter++;
setTimeout(f, interval);
}

function stop() {
v1 = c1; v2 = c2; v3 = c3;
counter = 0;
if (v1 == 6 && v2 == 6) { v1 = v3; v2 = v3; }
else if (v1 == 6 && v3 == 6) { v1 = v2; v3 = v2; }
else if (v2 == 6 && v3 == 6) { v2 = v1; v3 = v1; }
else if (v1 == 6) { v1 = Math.max(v2, v3); }
else if (v2 == 6) { v2 = Math.max(v1, v3); }
else if (v3 == 6) { v3 = Math.max(v1, v2); }
add = 0;
if (v1 == 0 && v2 == 0 && v3 == 0) {
  add = -credits;
} else if (v1 == 1 && v2 == 1 && v3 == 1) {
  add = 5;
} else if (v1 == 2 && v2 == 2 && v3 == 2) {
  add = 10;
} else if (v1 == 3 && v2 == 3 && v3 == 3) {
  add = 50;
} else if (v1 == 4 && v2 == 4 && v3 == 4) {
  add = 100;
} else if (v1 == 5 && v2 == 5 && v3 == 5) {
  add = 500;
} else if (v1 == 6 && v2 == 6 && v3 == 6) {
  add = 1000;
}
if (add > 0) add = bet * add;
credits += add;
if (bet > credits) bet = credits;
if (add >= 0) paid = add; 
if (add < 0) paid = "XXX";
show();
if (add > 0) alert("$"+add+"!");
if (credits <= 0) alert("Game Over!");
spinning = false;
}
