description:
Help Jared Go To College:
The Adventure Game
(based on a true story)

Young Jared has graduated high school. Can you help him go to college? Start by going home and talking to dad!































bs = document.tf.but;
lo = document.getElementById("loc");
pe = document.getElementById("per");
wh = document.getElementById("dw");
ta = document.getElementById("dt");
talked = new Array(6);
people = new Array('Charles Kushner, father of Jared', 'Hyman Rabinowitz, esteemed rabbi', 'Ivanka Trump, rich(?) heiress', 'Dr. Howard C. Jameson III, Dean of Admissions', 'Jane Marie Warner, JD, Dean of Admissions', 'Jimbo "The Funnel", Dean of Admissions');
phrase = new Array("You need to go to college, son. Have you tried visiting some universities?", "Praying is not gonna help you go to college... Are you meshuggenah?", "Maybe I will date you... that will show dad!", "I'm sorry, your grades are not high enough for Harvard.", "I'm sorry, your SATs are not high enough for Princeton.", "I'm sorry, but you're just not Arizona State material.", "Have you visited at least three schools yet?", "You know... I'm just gonna donate $2.5 million to Harvard.", "LEAVE ALREADY!", "Now you're Harvard material!", "You're kinda cute... for a Jew!", "I only date college graduates!", "Your family is loaded, right?", "I could use a new pair of shoes... or ten!", "Go away! Oh, sorry, I thought you were the doorman.");
place = 0;
dir = 'img/other/hjgts/';
father = 0;
ivanka = -1;
start = false;
reset();

function reset() {
  bs[0].disabled = false;
  for (i=1;i<6;i++) bs[i].disabled = true;
}

function goto(x) {
 if (!start) {
  for (i=1;i<6;i++) bs[i].disabled = false;
  hideDiv(null, "di");
  showDiv(null, "loc");
  showDiv(null, "per");
  showDiv(null, "bt");
  start = true;
 }
 xx = x-1;
 if (father != 3) bs[place].disabled = false;
 bs[xx].disabled = true;
 place = xx;
 lo.src = dir + 'loc' + x + '.jpg';
 pe.src = dir + 'per' + x + '.jpg';
 dt.innerHTML = '';
 dw.innerHTML = people[xx];
}

function talk() {
 y = place;
 talked[y] = true;
 if (place == 0) {
  if (father == 0) {
   father = 1;
  } else if (father == 1) {
   visited = talked[3] && talked[4] && talked[5];
   y = 6;
   if (visited) { y = 7; father = 2; } 
  } else {
   y = 8;
  }
 } else if (place == 2) {
   if (father != 3) {
    ni = ivanka;
    while (ni == ivanka) ni = parseInt(Math.random()*5) + 10;
    y = ni;
    ivanka = ni;
   }
 } else if (place == 3) {
  if (father >= 2) {
   y = 9;
   father = 3;
   for (i=0;i<2;i++) bs[i].disabled = true;
   for (i=3;i<6;i++) bs[i].disabled = true;
  }
 }
 say(y);
}

function say(x) {
 dt.innerHTML = '"' + phrase[x] + '"';
}
