description:
It's time to play... "NBA Player or Lawyer"! Can you guess strictly based on a person's name?

























first = "Jerry Fleishman|262|1947-53|Warriors";
base = new Array(
"Ossie Schectman|54|1947|Knicks",
"Nat Frankel|6|1947|Ironmen",
"Moe Becker|43|1947|Ironmen, Celtics, and Falcons",
"Ben Goldfaden|2|1947|Capitols",
"Mel Hirsch|13|1947|Celtics",
"Hank Lefkowitz|24|1947|Rebels",
"Nat Militzok|57|1947|Knicks and Huskies",
"Hank Rosenstein|60|1947|Knicks and Steamrollers",
"Ralph Kaplowitz|105|1947-48|Warriors and Knicks",
"Jack Garfinkel|92|1947-49|Celtics",
"Irv Rothenberg|131|1947-49|Knicks and four other teams",
"Irv Torgoff|147|1947-49|Capitols, Bullets, and Warriors",
"Sonny Hertzberg|293|1947-51|Knicks, Capitols, and Celtics",
"Max Zaslofsky|540|1947-56|Knicks and four other teams",
"Saul Mariaschin|43|1948|Celtics",
"Red Klotz|11|1948|Bullets",
"Sid Tanenbaum|70|1948-49|Knicks and Bullets",
"Herb Krautblatt|10|1949|Bullets",
"Phil Farbman|48|1949|Warriors and Celtics",
"Marv Schatzman|34|1950|Bullets",
"Hary Boykoff|109|1950-51|Hawks, Celtics, and Blackhawks",
"George Feigenbaum|17|1950-53|Bullets and Hawks",
"Irv Bemoras|131|1954-57|Hawks",
"Boris Nachamkin|6|1955|Royals",
"Lennie Rosenbluth|82|1958-59|Warriors"
);
last1 = "Leo Gottlieb|84|1947-48|Knicks";
last2 = "Don Forman|44|1949|Lakers";
last3 = "Jack Molinas|32|1954|Pistons";
count = 10;
cur = 0;
data = new Array(count);
gn = document.getElementById('gameName');
gt = document.getElementById('gameText');

restart();

function restart() {
  cur = 0;
  divShow("but1");
  divHide("but2");
  gn.innerHTML = "";
  base = randomizeArray(base);
  data[0] = first;
  for (i=1;i<count-3;i++) data[i] = base[i];
  data[count-3] = last1;
  data[count-2] = last2;
  data[count-1] = last3;
  show();
}

function show() {
  split = data[cur].split("|");
  gn.innerHTML = split[0];
  cur++;
}

function answer(x) {
  t = "Wrong! ";
  if (x == 0 || cur > count-3) t = "Correct! ";
  d = split[0] + " played " + split[1] + " games in " + split[2] + " for the " + split[3] + ".";
  if (cur == 1) d += " He won the first ever NBA championship! (It wasn't even called the NBA back then.)";
  if (cur == count-2) d += " But another Leo Gottlieb was the president of the New York Lawyers' Association in the early 1960s.";
  if (cur == count-1) d += " After retiring from basketball, he became a tax attorney.";
  else if (cur == count) d += " He was also a career criminal... and a lawyer.";
  gt.innerHTML = t + d;
  if (cur < count) {
    show();
  } else {
    divHide("but1");
    divShow("but2");
  }
}
