description:
Choose Your Own Adventure With JONJ!



















count = 0;
age = new Array(1, 5, 10, 15, 20)
eve = new Array("Your mom bakes a huge birthday cake.", "Your parents take you do McDonald's.", "Your kickball teammates are having a pizza party.", "Your high school girlfriend dumps you.", "Judd Apatow invites you to a barbecue.");
op1 = new Array("Eat the whole cake", "Eat five happy meals and cry until you get a sixth", "Eat ten slices; take a break, eat ten more", "Eat a tub of ice cream", "Drink 15 beers and eat 20 cheeseburgers");
op2 = new Array("Take a slice", "Ask your parents to get sushi instead", "Have a salad", "Punch your fist through a wall", "Listen to what he has to say");
fin = new Array("You grow up to be a doctor!", "You grow up to be a lawyer.", "You grow up to be an accountant.", "You grow up to be an insurance salesman.", "You become a major movie star. Unbelievable, isn't it?");
dea = document.getElementById("death");
advance();
for (i=1;i<=6;i++) {
jive = new Image;
jive.src = img(i);
}
function advance() {
display('eveDiv', 'Age ' + age[count] + ': '+ eve[count]);
ff.b1.value = op1[count];
ff.b2.value = op2[count];
count++;
}
function choose(x) {
if (x == 1 && count == 5) {
display('eveDiv', 'You DIE from a heart attack!');
dea.src = img(6);
hideBlock('butDiv');
showBlock('endDiv');
} else if (x == 1) {
advance();
} else if (x == 2) {
display('eveDiv', fin[count-1]);
dea.src = img(count);
hideBlock('butDiv');
showBlock('endDiv');
} else if (x == 3) {
count = 0;
hideBlock('endDiv');
showBlock('butDiv');
advance();
}
}
function img(x) {
return 'img/other/jh/jonah'+x+'.jpg';
}
function display(f, s) {
spa = document.getElementById(f);
if (spa != null) spa.innerHTML = s;
}
function showBlock(n) {
v = eval(n);
v.style.display = 'inline';
}
function hideBlock(n) {
v = eval(n);
v.style.display = 'none';
}
