description:
Hi, my name is Justin!





cur = 0;
text = new Array(
"Hi, my name is Justin!",
"My favorite food is macaroni and cheese!",
"I sing songs about love and friendship!",
"Girls like me!",
"Some boys like me too... but it's not for me to judge, they'll be judged by God!",
"I wrote a book!",
"It has 240 pages... some of them even have words!",
"I recently went to Israel... it was nice!",
"They made me wear a funny beanie!",
"I even got a tattoo in the Jewish language!",
"Don't worry, I didn't convert to Jew... My tattoo says 'Jesus'!",
"Love, Justin!");
ln = text.length;
tx = document.getElementById("txt");
pc = document.getElementById("pic");
but = document.ff.btn;
function next() {
 cur++;
 txt.innerHTML = text[cur].replace('.. ', '..<br>');
 pc.src = 'img/other/jb/bieber'+cur+'.jpg';
 if (cur == ln-1) {
  cur = -1;
  but.value = 'Restart';
 } else if (cur == 0) {
  but.value = 'Next';
 }
}
