var mx = new Array(-1, 2000, 500, 750, 200, 1000, 250, 700, 1500, 3000);
var isOut = new Array(6);
var cash, percent, what, wi;
clearOut();
function toInt(b) {
  v = parseInt(b.value);
  if (isNaN(v) || v < 0) { b.value = '0'; return 0; }
  return v;
}
function setText(d, t) {
  document.getElementById(d).innerHTML = t;
}
function hideButton(n, b) {
  if (b) divHide(n); else divShow(n);
}
function getOut(x) {
  r = parseInt(Math.random() * 5);
  s = null;
  t = "I'm out!"
  if (x == 1 && r == 0) s = "I don't doubt your ability, but";
  if (x == 1 && r == 1) s = "This is a terrible idea!";
  if (x == 1 && r == 2) s = "This is a real challenge.";
  if (x == 1 && r == 3) s = "This business is not ready for investment.";
  if (x == 1 && r == 4) s = "I will not get my money back.";
  if (x == 2 && r == 0) s = "I don't think this will sell on QVC.";
  if (x == 2 && r == 1) s = "This idea is not scalable.";
  if (x == 2 && r == 2) s = "This market is too competitive.";
  if (x == 2 && r == 3) s = "I'm not sure about the market for this.";
  if (x == 2 && r == 4) s = "I always know if it's a hero or a zero... This is a zero.";
  if (x == 3 && r == 0) s = "You are dead to me!";
  if (x == 3 && r == 1) s = "I'm gonna squish you like the cockroach you are.";
  if (x == 3 && r == 2) s = "This is a piece of crap.";
  if (x == 3 && r == 3) s = "I'm not here to make friends, I'm here to make money.";
  if (x == 3 && r == 4) s = "There's nothing proprietary about this.";
  if (x == 4 && r == 0) s = "This is not a licensing deal.";
  if (x == 4 && r == 1) s = "The risk is too high.";
  if (x == 4 && r == 2) s = "It's too early in the process.";
  if (x == 4 && r == 3) s = "This is not realistic.";
  if (x == 4 && r == 4) s = "You don't have your eye on the prize.";
  if (x == 5 && r == 0) s = "I don't have time to babysit you.";
  if (x == 5 && r == 1) s = "I need more skin in the game.";
  if (x == 5 && r == 2) s = "Let me simplify things.";
  if (x == 5 && r == 3) s = "The reward is not there relative to the risk.";
  if (x == 5 && r == 4) s = "I hate it.";
  if (s == null) return t;
  return s + " " + t;
}
function getIn(x) {
  r = parseInt(Math.random() * 8);
  if (r == 1) return "Congratulations!";
  if (r == 2) return "We'll make this work!";
  if (r == 3) return "We have a deal!";
  if (r >= 4) {
  if (x == 1) return "We're going to make a lot of money together!"
  if (x == 2) return "You'll be on QVC in a week!"
  if (x == 3) return "Great deal, you won't regret going with Mr. Wonderful!"
  if (x == 4) return "I did a deal once with a rabbi that worked out, so this will too!";
  if (x == 5) return "This is gonna be great."
  }
  return "DEAL!";
}
function restart() {
  document.ff.s1.selectedIndex = 0;
  document.ff.s2.selectedIndex = 0;
  document.ff.s3.selectedIndex = 0;
  divHide("tank");
  divShow("opening");
  clearOut();
}
function clearOut() {
  for (i=1;i<=5;i++) isOut[i] = false;
}
function enter() {
  cash = toInt(document.ff.s1);
  pct = toInt(document.ff.s2);
  wi = toInt(document.ff.s3);
  if (cash <= 0 || pct <= 0 || wi <= 0 || pct >= 100) {
    alert("Please enter valid values");
    return false;
  }
  what = document.ff.s3.options[wi].text;
  setText("cs", cash);
  setText("ps", pct);
  setText("ws", what);
  eval();
  divHide("opening");
  divShow("tank");
}
function eval() {
  tot = cash * (100 / pct);
  mv = mx[wi];
  allOut = (tot > mv || pct < 10);
  for (i=1;i<=5;i++) {
    o = getOut(i);
    if (wi == 1 && i != 2 && i != 1) isOut[i] = true;
    if (wi == 2 && i != 3 && i != 1 && i != 5) isOut[i] = true;
    if (wi == 3 && i != 2 && i != 3) isOut[i] = true;
    if (wi == 4 && i != 4) isOut[i] = true;
    if (wi == 5 && i != 2) isOut[i] = true;
    if (wi == 6 && i != 2 && i != 4) isOut[i] = true;
    if (wi == 7 && i != 2 && i != 1) isOut[i] = true;
    if (wi == 8 && i != 5 && i != 1) isOut[i] = true;
    if (wi == 9 && i != 5 && i != 3) isOut[i] = true;
    if (allOut) isOut[i] = true;
    bad = isOut[i];
    if (bad) setText("text"+i, o);
    else {
     c = makeCash(i, cash);
     q = makePercent(i, pct);
     t = "My offer is $"+c+",000 for "+q+"%";
     if (i == 3) {
       k = parseInt(Math.random() * 3) + 1;
       t += ", plus $"+k+" in perpetuity for each unit sold.";
     } else t += ".";
     setText("text"+i, t);
    }
    hideButton("b"+i, bad);
    hideButton("a"+i, bad);
  }
  showRestart();
}
function makePercent(x, p) {
  r = parseInt(Math.random() * 4);
  m = 1.25;
  if (r == 1) m = 1.5;
  else if (r == 2) m = 1.75;
  else if (r == 3) m = 2;
  v = parseInt(p * m);
  if (x == 5) v += 10;
  if (v > 60) v = 60;
  m = v % 10;
  if (m >= 1 && m <= 4) v = 10*parseInt(v / 10) + 5;
  if (m >= 6 && m <= 9) v = 10*parseInt(v / 10) + 10;
  return v;
}
function makeCash(x, p) {
  r = parseInt(Math.random() * 3);
  m = 1;
  if (r == 1) m = .5;
  else if (r == 2) m = .75;
  v = parseInt(p * m);
  if (v <= 0) v = 1;
  return v;
}
function showRestart(force) {
  hasIn = false;
  for (i=1;i<=5;i++) if (!isOut[i]) hasIn = true;
  if (!hasIn || force) divShow("rb");
  else divHide("rb");
}
function negotiate(x) {
  isOut[x] = true;
  o = getOut(x);
  setText("text"+x, o);
  hideButton("b"+x, true);
  hideButton("a"+x, true);
  showRestart();
}
function agree(x) {
  o = getIn(x);
  setText("text"+x, "<b>"+o+"</b>");
  for (i=1;i<=5;i++) {
    hideButton("b"+i, true);
    hideButton("a"+i, true);
  }
  showRestart(true);
}
