description:
Are you Jewish?

Are you blessed cursed with a long, difficult, tongue-twisting name?

If so, try our Make-Your-Name-Hip Generator*!




 Male
 Female







*Hipness not guaranteed

function generate() {
v = document.rf.one.value;
v = v.toLowerCase();
male = document.rf.sex[0].checked;
names = v.split(' ');
if (names == null || names.length != 2) {
alert("Please enter first and last name, separated by a space");
return;
}
fo = names[0]; lo = names[1];
if (!isAlpha(fo) || !isAlpha(lo)) {
alert("Please use A-Z only");
return;
}
fn = '', ln = '';
for (i=0;i<fo.length;i++) {
c = fo.charAt(i);
if (fn.length == 0 && male == isConsonant(c)) fn = c.toUpperCase();
if (fn.length == 1 && male == isVowel(c)) fn += c;
if (fn.length == 2 && male == isConsonant(c)) { fn += c; break; }
}
if (male) {
if (fn.length == 1) fn += 'an';
if (fn.length == 2) fn += 'n';
} else {
if (fn.length == 1) fn += 'na';
if (fn.length == 2) fn += 'a';
}
fc = false;
for (i=0;i<lo.length;i++) {
c = lo.charAt(i);
if (ln.length == 0) {fc = isConsonant(c); ln = c.toUpperCase(); }
if (ln.length == 1 && ((isVowel(c) && fc) || (isConsonant(c) && !fc))) { ln += c; break; }
}
if (ln.length == 1 && fc) ln += 'a';
if (ln.length == 1 && !fc) ln += 'y';
for (i=0;i<lo.length;i++) {
c = lo.charAt(lo.length-i-1);
if ((fc && isConsonant(c)) || (!fc && isVowel(c))) { ln += c; break; }
}
document.rf.two.value = fn + ' ' + ln;
}
function isAlpha(str) {
var re = /[^a-zA-Z]/g
return !(re.test(str));
}
function isVowel(c) {
return (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u');
}
function isConsonant(c) {
return !isVowel(c);
}
