answers = new Array(answer0, answer1, answer2, answer3, answer4, answer5, answer6, answer7, answer8, answer9, answer10, answer11);

responses = new Array("Awful! Are you meshugenah?", "Oy, gevalt!", "Better go back to yeshiva!", "That score could be worse!", "You're a real mensch!");

over = false;
num = 5;
sel = new Array(num);
ans = new Array(num);
myAnswers = new Array(num);
restart();
curQ = 0;

function restart() {
  over = false;
  document.ff.ai.value = '';
  document.ff.ab.value = 'Submit';
  showBox(false);
  chooseQuestions();
  showCurrentQuestion();
}

function chooseQuestions() {
  curQ = 0;
  used = new Array(num);
  for (i = 0; i < num; i++) { sel[i] = -1; ans[i] = -1; }
  for (i = 0; i < num; i++) {
    q = parseInt(Math.random() * questions.length);
    while (used[q]) {
      q++;
      if (q == questions.length) q = 0;
    }
    used[q] = true;
    sel[i] = q;
    ans[i] = answers[q];
  }
}

function showCurrentQuestion() {
  qd = document.getElementById("tdq");
  qd.innerHTML = questions[sel[curQ]];
}

function displayAnswers() {
  showBox(true);
  for (idx = 0; idx < num; idx++) {
    da = document.getElementById("ans"+idx);
    mya = myAnswers[idx].toUpperCase();
    da.innerHTML = mya;
  }
  da = document.getElementById("ans5");
  da.innerHTML = "<b>TOTAL</b>";
}

function displayScores() {
  totalScore = 0;
  for (idx = 0; idx < num; idx++) {
    ds = document.getElementById("sc"+idx);
    score = getScoreAll(myAnswers[idx], ans[idx]);
    if (score < 0) {
      da = document.getElementById("ans"+idx);
      da.innerHTML += ' <font color=yellow><i>#1!</i></font>';
      score = -score;
    }
    totalScore += score;
    ds.innerHTML = score;
  }
  ds = document.getElementById("sc5");
  ds.innerHTML = "<b>"+totalScore+"</b>";
  ri = parseInt((totalScore - 50) / 25);
  if (ri < 0) ri = 0;
  if (ri >= responses.length) ri = responses.length - 1;
  ds = document.getElementById("resp");
  ds.innerHTML = responses[ri];
}

function answer() {
  if (over) {
    restart();
    return false;
  }
  val = document.ff.ai.value;
  val = val.trim();
  if (val == '') val = 'PASS';
  if (val.indexOf('<') != -1 || val.indexOf('>') != -1) val = 'PASS';
  myAnswers[curQ] = val;
  document.ff.ai.value = '';
  curQ++;
  if (curQ == num) {
    document.ff.ai.value = '';
    document.ff.ab.value = 'Restart';
    displayAnswers();
    displayScores();
    over = true;
    return false;
  }
  showCurrentQuestion();
  return false;
}

function isMatch(s, t) {
  if (s == '' || s == 'PASS') return false;
  splitOff = t.split(':');
  words = splitOff[0];
  words = words.replace(/ /g,'').toLowerCase();
  word = s.replace(/ /g,'').toLowerCase();
  if (word == words) return true;
  if (words.indexOf('|') == -1) return false;
  possible = words.split('|');
  for (i = 0; i < possible.length; i++) {
    if (possible[i] == word) return true;
  }
  return false;
}

function getScore(s, t) {
  ok = isMatch(s, t);
  if (!ok) return 0;
  splitOff = t.split(':');
  score = parseInt(splitOff[1]);
  return score;
}

function getScoreAll(s, a) {
  for (ain = 0; ain < a.length; ain++) {
    sc = getScore(s, a[ain]);
    if (sc > 0) {
      if (ain == 0) return -sc;
      return sc;
    }
  }
  return 0;
}

function showBox(b) {
  bx = document.getElementById("tab");
  bx.style.display = b ? 'inline' : 'none';
}
