userName = '';
userId = null;
cont = new Array(4);
bids = new Array(4);
prizes = new Array("Woody Allen DVD Collection", "Heroes of the Torah Glasses, set of 36", "5-day, 4-night trip to Boca Raton, Florida", "Membership in Macaroon of the Month Club", "America's Heart Artwork by Yakov Smirnoff", "18 Carat Gold Pendant", "Praying Rabbis Figurine", "100% Kosher Wig", "Sterling Silver Menorah", "4 Front-Row Barbra Streisand Tickets", "50 lbs of Dead Sea Salt", "Conveyor Bagel Oven", "Sedar Plate and Matzah Holder", "The Complete Talmud", "Wide Band Hebrew Watch");
prices = new Array(150, 180, 1691, 192, 278, 585, 625, 650, 863, 1150, 318, 994, 305, 2100, 330);
pl = prizes.length;
pr = null;
prizeId = null;
bidIndex = 0;
bb = document.ff.bidBtn;
me = document.ff.myEntry;
win = -1;
games = 0;
sum = 0;
pa = new Array(pl);
pp = document.getElementById('prizePic');

//document.ff.myName.value = 'Lothar';
//restart();

function restart() {
userId = rand(4);
if (!user()) return;
randPrizes();
win = -1;
games = 0;
sum = 0;
begin();
contestants();
}

function begin() {
bidIndex = 0;
clearBids();
prize();
bidButton();
welcome.style.display = 'none';
game.style.display = 'inline';
}

function rand(x) {
return parseInt(Math.random()*x);
}

function randPrizes() {
for (p=0;p<pl;p++) pa[p] = p;
pa = pa.sort(randomSort);
}

function why() {
s = 'Nikki Ziering, who is Jewish through her ex-marriage to 90210 "star" Ian Ziering, was a Price is Right model for three years. Now enter your name and hit "Play!"';
document.getElementById("infoText").innerHTML = s;
}

function user() {
userName = document.ff.myName.value;
if (userName == '' || userName == null) {
  document.getElementById("infoText").innerHTML = "Please enter your name";
  return false;
}
setName(userId, userName.toUpperCase());
cont[userId] = userName.toUpperCase();
return true;
}

function setName(i, s) {
n = document.getElementById("name"+i);
n.innerHTML = s;
}

function setBid(i, s) {
n = document.getElementById("box"+i);
n.innerHTML = s;
}

function contestants() {
var nam = new Array("Schlomo", "Moishe", "Miriam", "Leah", "Rachel", "Rebecca", "Sarah", "Reuben", "Aaron", "Jacob", "Zev", "Ilan", "Ari", "Tali", "Golda", "Deborah", "Herschel", "Fievel", "Joshua", "Jonah", "Mordecai", "Eleazar", "Naomi", "Judith", "Esther", "Ben");
ns = nam.length;
var used = new Array(ns);
for (i=0;i<4;i++) {
  if (win > 0 && i != win) continue;
  if (i == userId) continue;
  q = -1;
  while (q == -1 || used[q]) {
    q = rand(ns);
  }
  used[q] = true;
  cont[i] = nam[q];
  setName(i, cont[i]);
}
}

function randomSort(a,b) {
return(parseInt(Math.random()*10)%2);
}

function prize() {
if (pr == null) {
  pr = rand(prizes.length);
} else {
  pr++;
  if (pr >= prizes.length) pr = 0;
}
prizeId = pa[pr];
n = document.getElementById("desc");
n.innerHTML = "You are bidding on <b>" + prizes[prizeId] + ".</b> ";
pp.src = "img/other/tpir/prize"+(prizeId+1)+".jpg";
}

function checkBid(x, val) {
if (isNaN(val)) return false;
for (y=0;y<x;y++) {
  if (val == bids[y]) return false;
}
return true;
}

function bidButton() {
s = 'none';
if (bidIndex == 20) v = "Restart!";
else if (bidIndex == userId) { v = "Place Bid!"; s = 'inline'; }
else if (bidIndex == 10) v = "New Prize!";
else if (bidIndex >= 4) v = "Announce Winner!";
else v = "Start Bidding!";
bb.value = v;
entry.style.display = s;
}

function bid() {
if (bidIndex == 20) return restart();
if (bidIndex == 10) return begin();
if (bidIndex >= 4) return winner();
if (bidIndex == userId) return placeBid();
sta = bidIndex;
end = (bidIndex < userId) ? userId : 4;
maxBid = prices[prizeId] * 1.5;
tries = 0;
for (i=sta;i<end;i++) {
  ok = false;
  while (!ok) {
    r = rand(maxBid)+1;
    if (tries < 10 && r > 10) {
      r = parseInt(r / 10) * 10;
    }
    if (i == 3 && tries < 10) {
      q = rand(4);
      r = (q == 3) ? 1: bids[q] + 1;
    }
    ok = checkBid(i, r);
    if (i == 3 && !ok) {
      r = 1;
      ok = checkBid(i, r);
    }
    tries++;
  }
  bids[i] = r;
  setBid(i, r);
}
bidIndex = end;
bidButton();
}

function placeBid() {
myBid = parseInt(me.value);
ok = (myBid > 0 && myBid != '' && !isNaN(myBid) && myBid != null);
if (!ok) {
  alert("Please enter your bid.");
  return false;
}
ok = checkBid(bidIndex, myBid);
if (!ok) {
  alert("Your bid is already entered by someone else.");
  return false;
}
bids[bidIndex] = myBid;
setBid(bidIndex, myBid);
bidIndex++;
if (bidIndex < 4) bid();
bidButton();
}

function clearBids() {
me.value = '';
for (i=0;i<4;i++) setBid(i, "");
}

function winner() {
delta = 0; minDelta = 9999;
win = -1;
price = prices[prizeId];
for (i=0;i<4;i++) {
  delta = price - bids[i];
  if (delta >= 0 && delta < minDelta ) {
    win = i;
    minDelta = delta;
  }
}
if (win == -1) {
  alert("You have all overbid. Erase the bids, please.");
  clearBids();
  bidIndex = 0;
  bidButton();
  return;
}
if (win == userId) sum += price;
t = "The actual retail price is $" + price + ". The winner is... <b>" + cont[win] + "</b>!!! ";
bidIndex = 10;
games++;
if (games == 6) {
  bidIndex = 20;
  e = (sum == 0) ? '.' : '!';
  t += 'Your winnings total $'+sum+e;
  pp.src = "img/other/tpir/tpir.gif";
}
desc.innerHTML = t;
bidButton();
}
