match = new Array(clean.length);
function randomString(r) {
  r = '';
  for (i=0;i<20;i++) {
    r += chars.charAt(Math.floor(Math.random() * 26));
  }
  return r;
}
function getMatch(a, b) {
  if (a.length > b.length) a = a.substring(0, b.length);
  else if (b.length > a.length) b = b.substring(a, b.length);
  for (i=0;i<a.length;i++) {
    if (a.charAt(i) != b.charAt(i)) {
      return a.substring(0, i);
    }
  }
  return a;
}
function stripString() {
  for (i=0;i<clean.length;i++) {
    match[i] = clean[i].replace(/[^a-z]/gi, '').toUpperCase();
  }
}
record = 1;
counter = 1;
best = '';
d0 = document.getElementById("blurb");
d1 = document.getElementById("dump");
d2 = document.getElementById("current");
stripString();
stripString();
function generate(mx, ev) {
  ms = mx;
  if (ev != null && ev.shiftKey) ms *= 100;
  d0.style.display = 'none';
  d1.innerHTML = '';
  d2.innerHTML = "Monkeys #"+dec(counter)+"-"+dec(counter+ms-1)+" typing...";
  setTimeout('monkeys('+ms+')',10);
}
function monkeys(mx) {
  better = false;
  top = 0;
  save = new Array(4);
  save[0] = 0;
  for (w=0;w<mx;w++) {
    cw = counter+w;
    r = randomString();
    for (q=0;q<match.length;q++) {
      m = getMatch(r, match[q]);
      if (m.length > record) {
        better = true;
        output(cw, m, r, q, better);
        record = m.length;
      }
      if (!better && m.length == record && save[0] == 0) {
        save[0] = cw;
        save[1] = m;
        save[2] = r;
        save[3] = q;
      }
    }
  }
  counter += mx;
  if (!better && save[0] != 0) {
    output(save[0], save[1], save[2], save[3]);
  } else if (!better) {
    fail();
  }
}
function decorate(s1, s2) {
  return "<b>"+s2.substring(0, s1.length)+"</b>"+s2.substring(s1.length,15)+"...";
}
function output(c, m, r, q, b) {
  t = '<p>Monkey #' + dec(c) + ' matched ' + m.length + ' characters! "' + decorate(m, r) + '": <i>' + clean[q] + '</i><br>';
  if (b) best = t; else best += t;
  d1.innerHTML += t;
}
function showBest() {
  if (best == '') restart(100000);
  d1.innerHTML = best;
}
function fail() {
  d1.innerHTML += "<p>Could not match previous record...";
}
function restart(x) {
  record = 1;
  counter = 1;
  best = '';
  generate(x);
}
function dec(x) {
  return x.toLocaleString();
}
