hobbies = new Array("Loves most of her grandchildren", "Always says her prayers", "Recently lost her faith in God", "Can't wait to move to Florida", "Enjoys ^ and %", "Secretly loves bacon cheeseburgers", "Makes art out of macaroni", "Collects used VHS tapes", "Has been divorced # times", "Voted ''most frisky'' in her nursing home", "Just bought new dentures", "Clips coupons", "Does not know how to use the Internet", "Once met Larry King", "Bakes delicious hamentashen", "Donates to Israeli charities", "Has seen ''Fiddler on the Roof'' 3| times", "Four-time bingo champion", "Has not said a swear word in 5| years", "Tries not to yell anymore", "Worked as a $ for 4| years", "Tried sushi once", "Fan of the Brooklyn Dodgers", "Speaks fluent Yiddish", "Still has most of her teeth", "Volunteers at the local library", "Has a metal plate in her head", "Trims her mustache weekly", "Suffers from terrible insomnia", "Has never owned a television", "Afraid of {", "Lost a toe in a woodworking accident", "Auditioned to be an extra in ''Yentl''", "Favorite book is ''Exodus''");
numHobbies = hobbies.length;
usedHobbies = new Array(numHobbies);

pic = document.getElementById("tp");
text1 = document.getElementById("rn");
text2 = document.getElementById("rd");

male = false;

function reset() {
  if (male) names = namesM; else names = namesW;
  for (i=0;i<num;i++) used[i] = false;
  for (i=0;i<numHobbies;i++) usedHobbies[i] = false;
  count = 0;
}

function nextPic() {
  if (count == num) return -1;
  r = parseInt(Math.random() * num);
  while (used[r]) {
    r++;
    if (r >= num) r = 0;
  }
  used[r] = true;
  count++;
  return r;
}

function nextHobby() {
  hob = parseInt(Math.random() * numHobbies);
  while (usedHobbies[hob]) {
    hob++;
    if (hob >= numHobbies) hob = 0;
  }
  usedHobbies[hob] = true;
  h = hobbies[hob];
  if (male) h = h.replace(' her ', ' his ');
  if (h.indexOf('|') != -1) h = h.replace('|', parseInt(Math.random() * 10))
  if (h.indexOf('%') != -1) h = h.replace('%', randomElement(sports))
  if (h.indexOf('$') != -1) h = h.replace('$', randomElement(jobs))
  if (h.indexOf('#') != -1) h = h.replace('#', randomElement(numbers))
  if (h.indexOf('^') != -1) h = h.replace('^', randomElement(crafts))
  if (h.indexOf('{') != -1) h = h.replace('{', twoRandomElements(fears));
  return h;
}

function next() {
  q = nextPic();
  wm = male ? 'm' : 'w';
  pic.src = 'img/other/sr/'+wm+q+'.jpg';
  n = parseInt(Math.random() * names.length);
  nam = names[n];
  age = parseInt(Math.random() * 20) + 70;
  h = nextHobby();
  text1.innerHTML = nam + ", " + age;
  text2.innerHTML = h;
  if (count == num) reset();
}

function start() {
  male = (document.ff.fg.value != 'M');
  reset();
  next();
  divHide('intro');
  divShow('tinder');
}
