n = new Array("Braun", "Epstein", "Gordon", "Green", "Greenberg", "Kinsler", "Lieberthal", "Pederson", "Rosen", "Youkilis");
a = new Array(0, 4, 3, 5, 2, 8, 9, 6, 1, 7);
hr = new Array(344, 130, 202, 328, 331, 257, 150, 123, 192, 150);
v = new Array(10);
u = new Array(10);
cur = 0;
strike = 0;
score = 0;
checkPlace = 10;
as = new Array(10);
bs = document.getElementsByName("but");
st = new Array(3);
ya = document.getElementById("yao");
sd = document.getElementById("sdi");
setup();
reset();

function setup() {
  for (i=0;i<as.length;i++) {
    as[i] = document.getElementById('ans'+i);
  }
  for (i=0;i<st.length;i++) {
    st[i] = document.getElementById('str'+i);
  }
}

function reset() {
  cur = 0;
  strike = 0;
  score = 0;
  checkPlace = 10;
  disableAll(false);
  clearAnswers();
  sd.innerHTML = "";
  for (i=0;i<3;i++) st[i].innerHTML = "";
  ya.style.display = "none";
}

function giveup() {
  for (i=0;i<v.length;i++) {
    as[i].innerHTML = n[a[i]] + " (" + hr[a[i]] + ")";
  }
  ya.style.display = "none";
}

function disableAll(d) {
  for (i=0;i<bs.length;i++) {
    disable(i, d);
  }
}

function clearAnswers() {
  for (i=0;i<as.length;i++) {
    as[i].innerHTML = "";
    v[i] = "-1";
  }
}

function disable(x, d) {
  bs[x].disabled = d;
}

function pick(b, x) {
  while (v[cur] != -1) {
    cur++;
    if (cur == 10) break;
  }
  as[cur].innerHTML = n[x];
  v[cur] = x;
  u[cur] = b;
  disable(x, true);
  cur++;
  if (cur >= checkPlace) {
    check();
  }
}

function check() {
  score = 0;
  for (i=0;i<v.length;i++) {
    if (v[i] == a[i]) {
      as[i].innerHTML = n[v[i]] + " (" + hr[v[i]] + ")";
      score++;
    } else {
      v[i] = -1;
      u[i].disabled = false;
      as[i].innerHTML = "";
      checkPlace = i+1;
    }
  }
  cur = 0;
  if (score < 10) {
    st[strike].innerHTML = "<b>X</b>";
    strike++;
  } else {
    //sd.innerHTML = " ";
    ya.innerHTML = "You win!";
    ya.style.display = "inline";
    return;
  }
  sd.innerHTML = "Strike "+strike+"!";
  if (strike == 3) {
    sd.innerHTML = "You're out!";
    ya.style.display = "inline";
    disableAll(true);
  }
}
