var badMsg = new Array("I didn't marry you for your looks!", "You want me to look like a peasant?", "Come on, shmendrik, open that wallet!", "I grew up in a castle, not on a farm!");
var goodMsg = new Array("That's why I married you, my shmendrik!", "Ivanka's got NOTHING on me!", "Thanks, but I'm gonna need a second outfit!", "I guess it'll have to do...");
var wantMsg = new Array("I need more stuff!", "I want more stuff!", "Gimme more stuff!");
var budget = 3000, total = 0, rows = 4, maxCost = 3775;
var cost = new Array(rows);
var oldBut = new Array(rows);

function randomEl(a) {
  return randomElement(a);
}

function buy(tb, r, p) {
  tb.disabled = true;
  if (oldBut[r] != null) oldBut[r].disabled = false;
  oldBut[r] = tb;
  oldCost = cost[r];
  if (isNaN(oldCost)) oldCost = 0;
  total -= oldCost;
  cost[r] = p;
  total += p;
  writeTotal();
  t = (total < budget) ? "Checkout": "Over Budget!";
  document.getElementById("checkoutBut").value = t;
}

function checkout() {
  t = randomEl(wantMsg);
  da = document.getElementById("answerText");
  da.style.color = "black";
  if (hasAll()) {
    if (total < maxCost) {
      t = randomEl(badMsg) + "&#128078;";
    } else {
      t = randomEl(goodMsg) + "&#128077;";
      da.style.color = "magenta";
    }
  }
  da.innerHTML = t;
}

function writeTotal() {
  document.getElementById("budgetText").innerHTML = "$"+(budget-total);
  document.getElementById("totalText").innerHTML = "$"+total;
}

function hasAll() {
  for (i=0;i<rows;i++) {
    if (isNaN(cost[i]) || cost[i] == 0) return false;
  }
  return true;
}





