pics = new Array(document.getElementById("pic1"), document.getElementById("pic2"), document.getElementById("pic3"));
cells = new Array(document.getElementById("cell1"), document.getElementById("cell2"), document.getElementById("cell3"));
spans = new Array(document.getElementById("c1"), document.getElementById("c2"), document.getElementById("c3"), document.getElementById("c4"), document.getElementById("c5"));
tds = new Array(document.getElementById("td1"), document.getElementById("td2"), document.getElementById("td3"));
ws = new Array(document.getElementById("w1"), document.getElementById("w2"), document.getElementById("w3"), document.getElementById("w4"));
arr = new Array(0, 1, 2);
revealed = new Array(false, false, false);
stage = 0;
car = -1;
guess = -1;
switchGuess = -1;
win = new Array(0, 0);
lose = new Array(0, 0);
bot = false;
restart();
function restart() {
  for (i = 0; i < 3; i++) setColor(i, false);
  divHide("info3");
  divShow("info1");
  arr = randomizeArray(arr);
  revealed = new Array(false, false, false);
  car = arr[0];
  pics[car].src = 'img/other/lmad/car.jpg';
  gr = parseInt(Math.random() * 2);
  g1 = 2; g2 = 3;
  if (gr == 1) { g1 = 3; g2 = 2; }
  pics[arr[1]].src = 'img/other/lmad/goat'+g1+'.jpg';
  pics[arr[2]].src = 'img/other/lmad/goat'+g2+'.jpg';
  flip(0, false);
  flip(1, false);
  flip(2, false);
  stage = 1;
}
function randomDoor() {
  rd = parseInt(Math.random() * 3);
  door(rd);
}
function randomChoice() {
  rc = parseInt(Math.random() * 2);
  doswitch = (rc == 1);
  if (doswitch) switchit();
  else reveal(false);
}
function flip(x, b) {
  cells[x].innerHTML = b ? '' : '' + (x + 1);
  pics[x].style.display = b ? 'inline' : 'none';
}
function door(x) {
  if (stage != 1) return;
  if (revealed[x]) return;
  revealed[x] = true;
  open = arr[1];
  if (x == open) open = arr[2];
  switchGuess = (3 - open - x);
  flip(open, true);
  divHide("info1");
  divShow("info2");
  spans[0].innerHTML = "" + (open + 1);
  spans[1].innerHTML = "" + (x + 1);
  spans[2].innerHTML = "" + (switchGuess + 1);
  setColor(x, true);
  guess = x;
  stage = 2;
}
function setColor(x, fill) {
  tds[x].style.backgroundColor = fill ? '#FFFF00': '#FFFFFF';
}
function switchit() {
  setColor(guess, false);
  setColor(switchGuess, true);
  temp = guess;
  guess = switchGuess;
  switchGuess = temp;
  reveal(true);
}
bb = "";
function reveal(switched) {
  flip(guess, true);
  flip(switchGuess, true);
  divHide("info2");
  divShow("info3");
  correct = (guess == car);
  st = switched ? " to switch" : " to NOT switch";
  spans[3].innerHTML = correct ? "CORRECT" + st : "WRONG" + st;
  spans[4].innerHTML = "" + (car + 1);
  ci = !switched? 0 : 1;
  if (correct) {
    win[ci]++;
    ws[0].innerHTML = "" + win[0];
    ws[2].innerHTML = "" + win[1];
  } else {
    lose[ci]++;
    ws[1].innerHTML = "" + lose[0];
    ws[3].innerHTML = "" + lose[1];
  }
  stage = 3;
}
function robot() {
  bot = true;
  t = '';
  for (r = 0; r < 100; r++) {
    restart();
    randomDoor();
    randomChoice();
  }
  bot = false;
}
