names = new Array("Pope Benedict XVI", "Hitler Cat", "A jar of pickles", "This dog", "A chunk of cheese", "An ostrich", "A cactus", "A refrigerator", "Mr. Miyagi", "Harvey the Invisible Rabbit", "Batman", "Marie Antoinette", "Captain Caveman", "Confucius", "Sasquatch", "Manute Bol", "Sean Connery", "A circus bear", "The Little Mermaid", "Tom Brady",

"Loch Ness Monster", "Nicole Kidman", "Santa Claus", "ALF", "Marilyn Manson", "Pamela Anderson", "Fidel Castro", "Count Chocula", "Mister Ed", "Charlie Sheen");
num = names.length;
used = new Array(num);
profs = 10;
txt = document.getElementById("who");
img = document.getElementById("port");
tot = 0;
more(num-1);
function more(v) {
 k = parseInt(Math.random()*num);
 if (v != null) k = v;
 while (used[k]) {
  k++;
  if (k == num) k = 0;
 }
 used[k] = true;
 tot = tot + 1;
 pic = 'img/other/tc/pic'+(k+1)+'.jpg';
 nam = names[k];
 if (k >= num - profs) {
  n = nam.replace('.', '_').toLowerCase();
  n = n.replace(/ /g,'_')
  pic = 'img/people/'+n.charAt(0)+'/'+n+'.jpg';
 }
 img.src = pic;
 txt.innerHTML = nam;
 if (tot >= num) {
  for (i=0;i<num;i++) used[i] = false;
  tot = 0;
 }
}
