description:
In honor of the 10th birthday of our website, it's time to play... Jew or Not Jew (finally, right?), hosted by the original SNL Jew, Not a Jew host, Tom Hanks! Each random round has 20 questions. (For the purpose of this game, we're counting "Barely a Jew" as "Not a Jew".)

























Score: 0 / 0 (0%)





successFunction = 'loadData()';
gp = document.getElementById('gamePic');
gn = document.getElementById('gameName');
ca = document.getElementById('corAns');
ta = document.getElementById('totAns');
pc = document.getElementById('corPct');
ma = document.getElementById('ansMsg');
who = '';
bull = false;
verdict = '';
ac = 0; at = 0;
endat = 20;
used = new Array();
restart();
function restart() {
 divShow("but1");
 divHide("but2");
 who = '';
 bull = false;
 verdict = '';
 ac = 0; at = 0;
 used = new Array();
 writeScore();
 callUrl();
}
function callUrl() {
 loadUrl('load.jsp?ACT=1&RAND='+Math.random());
}
function hasId(x) {
 try {
  return (used.indexOf(x) != -1);
 } catch(err) {
  return false;
 }
}
function loadData() {
 a = retVal.split('|');
 id = parseInt(a[0]);
 if (hasId(id)) {
  callUrl();
  return;
 }
 used.push(id);
 gn.innerHTML = a[1];
 gp.src = 'img/'+a[2];
 who = a[1];
 verdict = a[3];
 bull = a[4];
}
function writeScore() {
 ca.innerHTML = ac;
 ta.innerHTML = at;
 sc = 0;
 if (at != 0) sc = (ac / at) * 100;
 pc.innerHTML = Math.round(sc);
}
function answer(x) {
 at++;
 t = 'Wrong! ';
 if (x == bull) {ac++; t = 'Correct! '; }
 an = (verdict == 'Jew' || verdict == 'Borderline Jew') ? 'a ' : '';
 t += who + '<br>is ' + an + verdict + '.';
 writeScore();
 ma.innerHTML = t;
 if (at < endat) {
  callUrl();
 } else {
  divHide("but1");
  divShow("but2");
 }
}
