var c4 = new Array("The Thing", "Krusy The Clown", "Fievel Mousekewitz", "Magneto", "Miracle Max", "Kyle Broflovski", "James Wilson", "Cher Horowitz", "Tim Whatley", "Hanukkah Harry", "Ross And Monica Geller", "Emmanuel Goldstein", "Larry Sanders", "Kitty Pryde", "Max Bialystock", "Mickey Goldmill", "The Wandering Jew");
var c5 = new Array("The Ghost Writer", "American Pastoral", "I Married a Communist", "The Human Stain", "Portnoy's Complaint", "The Plot Against America", "Sabbath's Theater", "Goodbye, Columbus", "Nemesis", "Operation Shylock");
var c6 = new Array("Natalie Portman", "Marlee Matlin", "Gwyneth Paltrow", "Elizabeth Taylor", "Norma Shearer", "Helen Hunt", "Simone Signoret", "Luise Rainer", "Judy Holliday");
var c7 = new Array("Jay Fiedler", "Julian Edelman", "Sid Luckman", "Lyle Alzado", "Ron Mix", "Taylor Mays", "Josh Rosen", "Sage Rosenfels", "Harris Barton", "Sam McCullum", "Igor Olshansky", "Lennie Friedman", "Ali Marpet");
var c8 = new Array("Bananas", "Sleeper", "Match Point", "Vicky Cristina Barcelona", "Hannah and Her Sisters", "Midnight in Paris", "Alice", "Crimes and Misdemeanors", "Melinda and Melinda", "Love and Death", "Annie Hall", "Manhattan", "Blue Jasmine", "Scoop");
var c9 = new Array("David Ben-Gurion", "Moshe Sharett", "Levi Eshkol", "Golda Meir", "Yitzhak Rabin", "Menachem Begin", "Yitzhak Shamir", "Shimon Peres", "Benjamin Netanyahu", "Ehud Barak", "Ariel Sharon", "Ehud Olmert", "Naftali Bennett", "Yair Lapid");
var c = new Array(c0, c1, c2, c3, c4, c5, c6, c7, c8, c9);
var indexes = new Array(c.length);
for (i=0;i<indexes.length;i++) indexes[i] = i;
q = 3;
m = 4;
var cat = new Array(q);
cur = 0;
que = 0;
var answers;
tdTit = document.getElementById("tc");
tdQue = document.getElementById("tq");
tdSco = document.getElementById("ts");
tdAns = document.getElementById("ta");
ab = document.ff.ans;
score1 = 0;
score2 = 0;
over = false;

function restart() {
  showDiv(null, "rq");
  hideDiv(null, "rr");
  cur = 0;
  que = 0;
  score1 = 0;
  score2 = 0;
  over = false;
  indexes = randomizeArray(indexes);
  for (i=0;i<q;i++) cat[i] = indexes[i];
  answers = curAnswers();
  writeCategory();
  writeQuestion();
  writeAnswer();
  writeTheScore("");
}

function curCat() {
  return cat[cur];
}

function curCatName() {
  return catNames[curCat()];
}

function curAnswers() {
  a = c[curCat()];
  a = randomizeArray(a);
  return a.slice(0, m);
}

function writeCategory() {
  tdTit.innerHTML = "<i>Category:</i> "+curCatName();
}

function writeQuestion() {
  ab.value = "";
  tdQue.innerHTML = spaceOut(removeVowels(answers[que]));
}

function writeScore(b) {
  t = b ? "CORRECT!" : "WRONG!";
  writeTheScore(t);
}

function writeTheScore(t) {
  tdSco.innerHTML = t + " Score: " + score1 + " / " + score2;
}

function writeAnswer(t) {
  ta.innerHTML = (t == null) ? "" : "<i>Last Answer:</i> " + t;
}

function lettersOnly(str) {
  if (str == null) return "";
  return str.toUpperCase().replace(/[^a-zA-Z]/g,"");
}

function removeVowels(str) {
  s = lettersOnly(str);
  return s.replace(/[AEIOU]/gi, '');
}

function spaceOut(str) {
  t = str.charAt(0);
  for (i=1; i<str.length; i++) {
    if (parseInt(Math.random()*4) == 0) t += " ";
    t += str.charAt(i);
  }
  return t;
}

function strEquals(s1, s2) {
  t1 = lettersOnly(s1);
  t2 = lettersOnly(s2);
  return (t1 == t2);
}

function result(t) {
  s = "";
  if (t == 0) s = "Absolutely awful!";
  else if (t <= 3) s = "Better study some more...";
  else if (t <= 6) s = "Can you do better than this?";
  else if (t <= 9) s = "Beginner's luck!";
  else if (t <= 11) s = "You belong on Only Connect!";
  else if (t == 12) s = "Perfection!!!";
  return s;
}

function guess(fail) {
  if (over) return false;
  v1 = ab.value;
  if (fail) v1 = "X";
  if (v1 == '') {
    alert("Please enter a response");
    return false;
  }
  v2 = answers[que];
  correct = strEquals(v1, v2);
  score2++;
  if (correct) score1++;
  writeScore(correct);
  writeAnswer(v2);
  que++;
  if (que == m) {
    que = 0;
    cur++;
    if (cur == q) {
      over = true;
      showDiv(null, "rr");
      hideDiv(null, "rq");
      writeTheScore(result(score1));
      return false;
    }
    answers = curAnswers();
    writeCategory();
  }
  writeQuestion();
  return false;
}

restart();
