function clearField(b) {
 b.value = '';
}
function sample(x) {
 sampled = true;
 document.getElementById("q0").innerHTML = runners[x];
 document.getElementById("q1").innerHTML = shows[x];
 document.getElementById("q2").innerHTML = actors[x];
 document.getElementById("q3").innerHTML = roles[x];
