}
function isFilled() {
 if (sampled) return true;
 for (i=0;i<5;i++) {
  v = document.getElementById("b"+i).value;
  if (v == '' || v.startsWith('(')) return false;
 }
 return true;
}
function doSend() {
 if (!isFilled()) {
  alert("Please enter all values"); 
  return;
 }
 d1.innerHTML = '<marquee>Sending...</marquee>';
 setTimeout('swap()', 5000);
}
function swap() {
 hideDiv(d1);
 d2.style.display = 'block';
}
function answer() {
