function loadPic(x) {
setImage('zaz/zaz'+(x+1));
}
function getRadio(r) {
for (i=0;i<r.length;i++) if (r[i].checked) return r[i].value;
return null;
}
function process() {
f = document.zf;
v1 = getRadio(f.q1);
v2 = getRadio(f.q2);
v3 = getRadio(f.q3);
v4 = getRadio(f.q4);
v5 = getRadio(f.q5);
if (v1==null || v2==null || v3==null || v4==null || v5==null) { alert("Please answer all questions"); return; }
d = document.getElementById("result");
who = new Array("David Zucker", "Jim Abrahams", "Jerry Zucker");
c = new Array(0, 0, 0, 0);
if (v1 == 1) { c[0] += 5; c[2] += 1; }
if (v2 == 1) { c[0] += 4; c[2] += 4; }
if (v2 == 0) { c[1] += 5; }
if (v3 == 1) { c[2] += 3; }
if (v4 == 1) { c[0] += 10; }
if (v4 == 0) { c[0] -= 2; }
q = 0;
if (c[1] > c[0]) q = 1;
if (c[2] > c[1] && c[2] > c[0]) q = 2;
txt = "You are "+ who[q] + "!";
d.innerHTML = txt;
loadPic(q);
}
